function addNew() {
	var e = document.createElement("tr");
	(e.className = "task"),
		(e.id = "task" + document.getElementById("tasks").children.length),
		(e.innerHTML = document
			.getElementById("newTaskContent")
			.innerHTML.replace(/cell/gi, "td")),
		document
			.getElementById("tasks")
			.insertBefore(e, document.getElementById("addNew").parentElement),
		document.getElementsByName("new").forEach(function () {
			this.name = document.getElementById("tasks").children.length;
		});
}
function noTask(e, t) {
	if (confirm("Delete task?\n\nTHIS IS PERMANENT AND CANNOT BE UNDONE")) {
		switch ((fade(e), t)) {
			case "delete":
				e.outerHTML = "";
				break;
			case "hide":
				e.style.display = "none";
				break;
			default:
				console.error(
					'Value of "method" parameter in function "noTask()" may only be "delete" or "hide". Value given: ' +
						t,
				);
		}
	}
}
function fade(e) {
	e.style.opacity = 1;
	var t = 0,
		n = setInterval(function () {
			t++, 100 === t && clearInterval(n), (e.style.opacity -= 0.01);
		}, 6);
}
